mapper.delete:
  	
  		DELETE reserve 
		 WHERE userno=#{userno}
		    or  re_no in (SELECT re.re_no
		                    FROM reserve re, room ro
		                   WHERE ro.userno=#{userno}
		                     and ro.roomno=re.roomno)

mapper.select:
  	
		SELECT count(*) 
		  FROM room 
		 WHERE userno=#{userno}

  	
		SELECT count(*) 
		  FROM user1 
		 WHERE userno=#{userno}
