mapper.select:
	select distinct * from room  where roomno in (
    select roomno day from reserve where
    not TO_DATE(#{start},'YYYYMMDD') between startday and endday 
	and not TO_DATE(#{end},'YYYYMMDD') between startday and endday) 
	and rcity = #{city}
	and rpeople >= #{people}

		
	    		SELECT * FROM room 
   					WHERE roomno NOT IN(SELECT distinct ro.roomno 
			            FROM reserve re RIGHT JOIN room ro
						ON re.roomno = ro.roomno
			            WHERE (TO_DATE(#{checkIn}, 'YYYYMMDD') BETWEEN re.startday AND re.endday) 
			            AND (TO_DATE(#{checkOut}, 'YYYYMMDD') BETWEEN re.startday AND re.endday))
				    AND rcity = #{rcity}
				    AND rpeople >= #{rpeople}
				    AND rtype = #{rtype}
				    AND rroom >= #{rroom}
				    AND rbed >= #{rbed}
				    AND rpet = #{rpet}
				    AND rsmoke = #{rsmoke}
				    AND #{minPrice}<=rprice AND rprice<=#{maxPrice}
