mapper.select:
		
	    		SELECT * FROM room 
   					WHERE roomno NOT IN(SELECT distinct ro.roomno 
			            FROM reserve re RIGHT JOIN room ro
						ON re.roomno = ro.roomno
			            WHERE (TO_DATE(#{checkIn}, 'YYYYMMDD') BETWEEN re.startday AND re.endday) 
			            AND (TO_DATE(#{checkOut}, 'YYYYMMDD') BETWEEN re.startday AND re.endday))
				    AND rcity = #{rcity}
				    AND rpeople >= #{rpeople}
				    AND rtype = #{rtype}
				    AND rroom >= #{rroom}
				    AND rbed >= #{rbed}
				    AND rpet = #{rpet}
				    AND rsmoke = #{rsmoke}
				    AND #{minPrice}<=rprice AND rprice<=#{maxPrice}

		
	    		SELECT COUNT(*) FROM room 
   					WHERE roomno NOT IN(SELECT distinct ro.roomno 
			            FROM reserve re RIGHT JOIN room ro
						ON re.roomno = ro.roomno
			            WHERE (TO_DATE(#{checkIn}, 'YYYYMMDD') BETWEEN re.startday AND re.endday) 
			            AND (TO_DATE(#{checkOut}, 'YYYYMMDD') BETWEEN re.startday AND re.endday))
				    AND rcity = #{rcity}
				    AND rpeople >= #{rpeople}
				    AND rtype = #{rtype}
				    AND rroom >= #{rroom}
				    AND rbed >= #{rbed}
				    AND rpet = #{rpet}
				    AND rsmoke = #{rsmoke}
				    AND #{minPrice}<=rprice AND rprice<=#{maxPrice}

		
    	SELECT * FROM
			(SELECT rownum AS rnum, a.*
				FROM (SELECT * FROM room 
   					WHERE roomno NOT IN(SELECT distinct ro.roomno 
			            FROM reserve re RIGHT JOIN room ro
						ON re.roomno = ro.roomno
			            WHERE (TO_DATE(#{checkIn}, 'YYYYMMDD') BETWEEN re.startday AND re.endday) 
			            AND (TO_DATE(#{checkOut}, 'YYYYMMDD') BETWEEN re.startday AND re.endday))
				    AND rcity = #{rcity}
				    AND rpeople >= #{rpeople}
				    AND rtype = #{rtype}
				    AND rroom >= #{rroom}
				    AND rbed >= #{rbed}
				    AND rpet = #{rpet}
				    AND rsmoke = #{rsmoke}
				    AND #{minPrice}<=rprice AND rprice<=#{maxPrice}) a)
		WHERE rnum>=#{startrow} AND rnum<=#{endrow}
